const perplexityResults = $input.all();
const results = [];

console.log('Perplexity raw results:', JSON.stringify(perplexityResults, null, 2));

for (const item of perplexityResults) {
  try {
    let content = '';

    // Sprawd藕 r贸偶ne mo偶liwe struktury odpowiedzi
    if (item.json?.answer) {
      content = item.json.answer;
    } else if (item.json?.response) {
      content = item.json.response;
    } else if (item.json?.content) {
      content = item.json.content;
    } else if (item.json?.text) {
      content = item.json.text;
    } else {
      console.log('Perplexity - nieznana struktura:', item.json);
      continue;
    }

    console.log('Perplexity content:', content);

    // Podziel na linie i znajd藕 artykuy
    const lines = content.split('\n').filter(line => line.trim());

    for (const line of lines) {
      const trimmedLine = line.trim();

      // Szukaj linii kt贸re wygldaj jak tytuy artyku贸w
      if (trimmedLine.match(/^\d+\./) || // "1. Tytu"
          trimmedLine.includes('[') ||     // "[Tytu]"
          trimmedLine.includes('http') ||  // linki
          (trimmedLine.length > 20 && trimmedLine.includes('AI'))) { // dugie linie z AI

        results.push({
          json: {
            source: 'Perplexity',
            title: trimmedLine.replace(/^\d+\.\s*/, '').replace(/^\[|\]$/g, ''),
            link: '',
            summary: trimmedLine,
            pubDate: new Date().toISOString(),
            sourceUrl: 'Perplexity AI'
            const linkMatch = trimmedLine.match(/(https?:\/\/[^\s]+)/);
link: linkMatch ? linkMatch[1] : '',
          }
        });
      }
    }

    // Jeli nie znalaz nic, dodaj ca odpowied藕 jako jeden item
    if (results.length === 0 && content.length > 10) {
      results.push({
        json: {
          source: 'Perplexity',
          title: 'Podsumowanie wiadomoci AI z Perplexity',
          link: '',
          summary: content.substring(0, 500) + '...',
          pubDate: new Date().toISOString(),
          sourceUrl: 'Perplexity AI'
        }
      });
    }

  } catch (error) {
    console.log('Bd parsowania Perplexity:', error);
  }
}

console.log('Perplexity parsed results count:', results.length);
return results;

